            this.stores = [];
            this.dummystores = [];
            console.log(error);
            this.util.apiErrorHandler(error);
        }).catch(error => {
            this.stores = [];
            this.dummystores = [];
            console.log(error);
            this.util.apiErrorHandler(error);
        });
    }
    getHomeDataWithGeoLocation() {
        const param = {
            lat: localStorage.getItem('userLat'),
            lng: localStorage.getItem('userLng')
        };
        this.api.post_public('v1/home/searchStoreWithGeoLocation', param).then((data) => {
            console.log(data);
