    getAddress(lat, lng) {
      if (typeof google === 'object' && typeof google.maps === 'object') {
        const geocoder = new google.maps.Geocoder();
        const location = new google.maps.LatLng(lat, lng);
        geocoder.geocode({
          'location': location
        }, (results, status) => {
          console.log(results);
          console.log('status', status);

          if (results && results.length) {
            localStorage.setItem('location', 'true');
            localStorage.setItem('address', results[0].formatted_address);
            this.util.deliveredAddress = results[0].formatted_address;
            this.util.publishCity(results[0]);
          } else {
            this.util.errorMessage('Something went wrong please try again later');
          }
        });
      } else {
        this.util.errorMessage(this.util.translate('Error while fetching google maps... please check your google maps key'));
        return false;
      }
    }

    navigationInterceptor(event) {
      if (event instanceof NavigationStart) {
        this.loading = true;
        this.loaded = false;
      }

      if (event instanceof NavigationEnd) {
        this.loading = false;
        this.loaded = true; // window.scrollTo(0, 0);

        window.scrollTo({
          top: 0
        });
        this.getHeaderTitle();
      }

      if (event instanceof NavigationCancel) {
        this.loading = false;
        this.loaded = true;
      }

      if (event instanceof NavigationError) {
        this.loading = false;
        this.loaded = true;
      }
    }

    getTitle(state, parent) {
      const data = [];

      if (parent && parent.snapshot.data && parent.snapshot.data.title) {
        data.push(parent.snapshot.data.title);
      }

      if (state && parent) {
        data.push(...this.getTitle(state, state.firstChild(parent)));
      }

      return data;
    }

